import React, { useState } from 'react';
import { auth } from '../firebaseConfig';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    // Verifica se o e-mail e a senha são os valores fixos
    if (email === 'bernardo123' && senha === 'bernardo1234') {
      try {
        // Autentica o usuário com Firebase
        await signInWithEmailAndPassword(auth, email, senha);
        navigate('/principal'); // Redireciona para a página principal
      } catch (error) {
        setError('Erro ao fazer login: ' + error.message);
      }
    } else {
      setError('E-mail ou senha incorretos.'); // Exibe mensagem de erro
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="email"
        placeholder="E-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Senha"
        value={senha}
        onChange={(e) => setSenha(e.target.value)}
      />
      {error && <p>{error}</p>}
      <button onClick={handleLogin}>Entrar</button>
      {/* Botão para redirecionar para o cadastro */}
      <button onClick={() => navigate('/cadastro')}>Registre-se</button>
      {/* Botão para redirecionar para a página principal */}
      <button onClick={() => navigate('/principal')}>Ir para Principal</button>
    </div>
  );
};

export default Login;
