import React, { useState } from 'react';
import { auth } from '../firebaseConfig';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [acessoSenha, setAcessoSenha] = useState(''); // Nova senha de acesso
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Autentica o usuário no Firebase Authentication
      await signInWithEmailAndPassword(auth, email, senha);
      
      // Verifica a senha de acesso adicional
      if (acessoSenha === 'senhaDeAcesso123') {
        navigate('/principal'); // Redireciona para a página principal
      } else {
        setError('Senha de acesso incorreta.');
      }
    } catch (error) {
      setError('Usuário não cadastrado ou senha incorreta.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="email"
        placeholder="E-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Senha"
        value={senha}
        onChange={(e) => setSenha(e.target.value)}
      />
      <input
        type="password"
        placeholder="Senha de Acesso"
        value={acessoSenha} // Campo de senha de acesso
        onChange={(e) => setAcessoSenha(e.target.value)}
      />
      {error && <p>{error}</p>}
      <button onClick={handleLogin}>Entrar</button>
      {/* Botão para redirecionar para o cadastro */}
      <button onClick={() => navigate('/cadastro')}>Registre-se</button>
    </div>
  );
};

export default Login;
