import React, { useEffect, useState } from 'react';
import { auth, db } from '../firebaseConfig';
import { doc, getDoc } from 'firebase/firestore';
import './Principal.css'; // Criar um arquivo de estilos para o jogo da velha

const Principal = () => {
  const [userData, setUserData] = useState(null);
  const [gameState, setGameState] = useState(Array(9).fill(null));
  const [isXNext, setIsXNext] = useState(true);

  // Função para buscar dados do usuário
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const docRef = doc(db, 'users', user.uid);
          const docSnap = await getDoc(docRef);
          if (docSnap.exists()) {
            setUserData(docSnap.data());
          } else {
            console.error("Documento não encontrado!");
          }
        } else {
          console.error("Usuário não autenticado!");
        }
      } catch (error) {
        console.error("Erro ao buscar dados do usuário:", error);
      }
    };
    fetchUserData();
  }, []);

  // Função para checar o vencedor do jogo
  const checkWinner = (squares) => {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  };

  // Função para lidar com o clique nas células do jogo
  const handleClick = (index) => {
    const squares = [...gameState];
    if (squares[index] || checkWinner(gameState)) return; // Se já tiver vencedor ou a célula estiver ocupada, ignore

    squares[index] = isXNext ? 'X' : 'O';
    setGameState(squares);
    setIsXNext(!isXNext); // Alterna entre X e O
  };

  // Verifica se há vencedor
  const winner = checkWinner(gameState);
  const status = winner ? `Vencedor: ${winner}` : `Próximo jogador: ${isXNext ? 'X' : 'O'}`;

  // Reiniciar o jogo
  const resetGame = () => {
    setGameState(Array(9).fill(null));
    setIsXNext(true);
  };



  return (
    <div>
      <h2>Bem-vindo, {userData.nome} {userData.sobrenome}</h2>
      <p>Data de Nascimento: {userData.dataNascimento}</p>

      <h3>Jogo da Velha</h3>
      <div className="game">
        <div className="game-board">
          {gameState.map((value, index) => (
            <button key={index} className="square" onClick={() => handleClick(index)}>
              {value}
            </button>
          ))}
        </div>
        <div className="status">{status}</div>
        <button className="reset-button" onClick={resetGame}>Reiniciar Jogo</button>
      </div>
    </div>
  );
};

export default Principal;
